import logo from './logo.svg';
import './App.css';
import {Map} from '../components/Map/MapboxMap';
import {Titlebar} from '../components/Titlebar/Titlebar';
import {Infobar} from '../components/Infobar/Infobar';
import {Searchbar} from '../components/Searchbar/Searchbar';
import {Navbar} from '../components/Navbar/Navbar';
import {MyForm} from '../components/Form/Form';
import React, { useState } from 'react';
import ReactDOM from "react-dom";
//import { Leaflet, Icon } from "leaflet";
//import 'leaflet/dist/leaflet.css';


const App = () => {
  const [markers, setMarkers] = useState([{
    lat: 51.505,
    long: -0.09
  }]);
  const [loading, setLoading] = useState(false);
  //debugging
  const [markersSlice, setMarkersSlice] = useState([{
    lat: 51.505,
    long: -0.09
  }]);
  const [coord, setCoord] = useState([51.505, -0.09]);

  // array of jsons -> array of [lat long]
  const restructureData = (arrJson) => {
    const arrCoord = arrJson.map(el => [el.lat, el.long]);
    setCoord(arrCoord);
  }

  const fetchMarkers = async() => {
    try {
      const response = await fetch("/allops");
      const data = await response.json();
      console.log("Fetch successful!");
      setMarkers(data);
    } catch (err) {
      console.log("There was a problem with backend connection");
      return;
    }
  }


  const fetchData = async() => {
    setLoading(true);
    await fetchMarkers();
    await setMarkersSlice(markers.slice(0,10));
    await setCoord(restructureData(markersSlice));
    //await setMarkersSlice(markers.slice(0,10));
    setLoading(false)
  }

  return ( 
    <div>
      {loading ? <div>Loading</div> : 
    <div>
    <Titlebar />
    <Navbar />
    <table width="100%">
      <tr>
        <td className="left-container"
        style={{backgroundColor:'#2F4989'}}>

        <MyForm />
        </td>
        <td className="map-container">
        <Searchbar fetchMarkers={fetchData}/>
        <Map markers={markersSlice}/>
        </td>
        <td>
          <div className="info-container">
        <Infobar num={1}/>
        <Infobar num={2}/>
        <Infobar num={3}/>
        <Infobar num={4}/>
        <Infobar num={5}/>
        <Infobar num={6}/>
        </div>
        </td>
      </tr>     
      </table> 
    </div>
    }
    </div>
   );
}

export default App;
