import React, { useState } from "react";
import { Polyline, Circle, Popup } from "react-leaflet";

export const MapLine = ({ lines, setActiveLine, activeLine }) => {
  return (
    <div>
      {lines.map(({ id, opFrom, opTo, lnumber }) => {
        return (
          <Polyline
            key={id}
            positions={[
              [opFrom.lat, opFrom.long],
              [opTo.lat, opTo.long],
            ]}
            color={"blue"}
            stroke={true}
            opacity={0.3}
            bubblingMouseEvents={false}
            weight={5}
            eventHandlers={{
              click: () => {
                console.log("line clicked");
                const activeLine = lines.filter((l) => l.lnumber === lnumber);
                setActiveLine(activeLine);
              },
            }}
          />
        );
      })}
    </div>
  );
};

export const MapActiveLine = ({ activeLine }) => {
  return (
    <div>
      {activeLine.map(({ id, opFrom, opTo }) => {
        return (
          <Polyline
            key={id}
            positions={[
              [opFrom.lat, opFrom.long],
              [opTo.lat, opTo.long],
            ]}
            color={"red"}
            stroke={true}
            opacity={1}
            bubblingMouseEvents={false}
            weight={10}
          />
        );
      })}
    </div>
  );
};

export const MapProblemLine = ({ line }) => {
  return (
    <div>
      {line.map(({ ops }) => {
        return (
          <Polyline
            positions={[
              [ops[0].lat, ops[0].long],
              [ops[1].lat, ops[1].long],
            ]}
            color={"black"}
            stroke={true}
            opacity={1}
            bubblingMouseEvents={false}
            weight={10}
          />
        );
      })}
    </div>
  );
};

export const MapConstrLine = ({ construct }) => {
  return (
    <div>
      {construct.map(({ operatingLines }) =>
        operatingLines.map(({ segment }) =>
          segment.map((seg) => {
            return (
              <div>
                <Polyline
                  positions={[
                    [seg.opFrom.lat, seg.opFrom.long],
                    [seg.opTo.lat, seg.opTo.long],
                  ]}
                  color={"red"}
                  stroke={true}
                  opacity={0.25}
                  bubblingMouseEvents={false}
                  weight={10}
                />
                <Popup>
                  <b>{seg.opFrom.name} </b> <br />
                  <p>{seg.opTo.name}</p>
                </Popup>
              </div>
            );
          })
        )
      )}
    </div>
  );
};

export const MapConstrPoint = ({ construct }) => {
  return (
    <div>
      {construct.map(({ operatingPoints }) =>
        operatingPoints.map(({ point }) => {
          return (
            <Circle
              center={{
                lat: point.lat,
                lng: point.long,
              }}
              fillColor="red"
              radius={3000}
            >
              <Popup>
                <b>{point.name} </b> <br />
                <p>{point.abbreviation}</p>
              </Popup>
            </Circle>
          );
        })
      )}
    </div>
  );
};
